#include<avr/io.h>
void T0Delay();
int main()
{
    DDRB=0xFF;
    while(1){
        PORTB=0x55;
        // delay size unknown
        T0Delay(); 
        PORTB=0xAA;
        T0Delay();
    }
}
void T0Delay()
{
    TCNT0=0x20;
    TCCR0=0x01;
    while((TIFR&0x1)==0);
    TCCR=0;
    TIFR=0x1
}
```
